/** 降采样 */
export default class Resampler {
  private workerUrl;
  private worker?;
  private onDataHandler;
  private messageHandler?;
  private sampleRate;
  private originalSampleRate?;
  /**
   * 构造函数
   * @param workerUrl 地址
   * @param sampleRate 输出采样率
   * @param onData encode 数据回调
   * @param options 可选项
   */
  constructor(workerUrl: string, sampleRate: number, onData: (chunk: Uint8Array) => void, options: any);
  /**
   * 初始化 task
   */
  init: () => Promise<void>;
  /**
   * 传递数据
   * @param buffers 原始数据
   */
  feed: (buffers: Float32Array[]) => void;
  /**
   * 销毁 worker
   */
  destroy: () => void;
}
